import React, { useState, useEffect } from "react";
import { style } from "../../styles/ParkDetail.styles";
import { Image } from "antd";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import { useParams } from "react-router-dom";
import GoogleMap from "./GoogleMap";
export default function ParkDetail() {
  let { id } = useParams();
  console.log(id);
  const [parkItemData, setparkItemData] = useState([]);
  useEffect(() => {
    fetchParkItem();
  }, []);
  const fetchParkItem = async () => {
    try {
      const response = await fetch(`http://localhost:3001/detail/${id}`);
      if (!response.ok) {
        throw new Error(
          `This is an HTTP error: The status is ${response.status}`
        );
      }
      let res = await response.json();
      console.log(res);
      setparkItemData(res);
    } catch (err) {
      console.log(err);
    }
  };
  console.log(parkItemData);

  return (
    <div className="container">
      <ParkTitle>
        {parkItemData.p_park}
        <i>
          <FontAwesomeIcon icon={faHeart} />
        </i>
      </ParkTitle>
      <div className="flex mb-30">
        <ParkImage>
          <Image
            width={349}
            height={313}
            src={parkItemData.p_img}
            fallback="data:image/png;base64,"
          />
        </ParkImage>
        <ParkMap>
          <GoogleMap />
        </ParkMap>
      </div>
      <div className="flex space-between mb-20">
        <ParkLink>홈페이지 : {parkItemData.template_url}</ParkLink>
        <ParkAddr>{parkItemData.p_addr}</ParkAddr>
      </div>
      <ParkDesc>{parkItemData.p_list_content}</ParkDesc>
    </div>
  );
}

const { ParkTitle, ParkImage, ParkMap, ParkLink, ParkAddr, ParkDesc } = style;
