import React, { useState } from "react";
import { Link } from "react-router-dom";
import { List, Card, Rate, Image } from "antd";
import { HeartOutlined, HeartFilled, HeartTwoTone } from "@ant-design/icons";

export const defaultHeaders = {
  "Content-Type": "application/json",
  Accept: "application/json",
};
export default function ParkCard({ item }) {
  defaultHeaders.Authorization = localStorage.getItem("accessToken");
  const { Meta } = Card;

  const [isLikeClicked, setIsLikeClicked] = useState(false);
  // const [pathCheck, serPathCheck] = useState(true);

  // const [itemPidx, setItemPidx] = useState(`/detail/${item.pidx}`);

  const handleClick = (e) => {
    const parentTarget = e.target.parentNode;
    // 공원좋아요
    if (
      e.target.getAttribute("data-icon") === "heart" ||
      parentTarget.getAttribute("data-icon") === "heart"
    ) {
      handleParkLike(item.pidx);
    }
  };

  const handleParkLike = async (pidx) => {
    if (isLikeClicked === false) {
      try {
        const response = await fetch(`/parks/${pidx}/good`, {
          method: "POST",
          headers: defaultHeaders,
          body: JSON.stringify({ pLikeCnt: 1 }),
        });
        setIsLikeClicked(true);
      } catch (error) {
        console.log(error);
        throw new Error("Failed to load data");
      }
    } else if (isLikeClicked === true) {
      try {
        const response = await fetch(`/parks/${pidx}/good`, {
          method: "DELETE",
          headers: defaultHeaders,
          body: null,
        });
        console.log(response);
        // console.log(response.body);
        // console.log(response.json());
        setIsLikeClicked(false);
      } catch (error) {
        console.log(error);
        throw new Error("Failed to load data");
      }
    }
  };

  return (
    <List.Item key={item.pidx} onClick={handleClick}>
      <Card
        hoverable
        cover={
          <Link to={`/detail/${item.pidx}`}>
            <Image
              preview={false}
              height={200}
              width={"100%"}
              src={item.pimg || "error"}
              alt={item.pname}
              fallback="data:image/png;base64,"
            />
          </Link>
        }
      >
        {isLikeClicked ? (
          <HeartFilled style={{ color: "hotpink", fontSize: "20px" }} />
        ) : (
          <HeartTwoTone twoToneColor="#eb2f96" style={{ fontSize: "20px" }} />
        )}
        <Meta title={<Link to={`/detail/${item.pidx}`}>{item.pname}</Link>} />
        <Rate disabled defaultValue={item.pavgRate} />
      </Card>
    </List.Item>
  );
}
