/* global kakao */
import React, { useRef, useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { Image } from "antd";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faHeart } from "@fortawesome/free-solid-svg-icons";
import useGetParkData from "../../hooks/useGetParkData";
import { style } from "./ParkDetail.styles";
const { kakao } = window;

export default function ParkDetail() {
  const { pidx } = useParams();
  const url = `https://parkproj.herokuapp.com/parks/${pidx}`;

  const { data, isLoading, hasError, errorMessage } = useGetParkData(url);

  useEffect(() => {
    if (data) {
      // console.log(data);
      const container = document.getElementById("kakaoMap");
      const options = {
        center: new kakao.maps.LatLng(data.py, data.px),
        level: 3,
      };

      const content = `<div>${data.pname}</div>`;

      const map = new kakao.maps.Map(container, options);

      //마커가 표시 될 위치
      let markerPosition = new kakao.maps.LatLng(data.py, data.px);

      // 마커를 생성
      let marker = new kakao.maps.Marker({
        position: markerPosition,
        text: data.pname,
      });

      // 마커를 지도 위에 표시
      marker.setMap(map);

      // 커스텀 오버레이를 생성합니다
      var customOverlay = new kakao.maps.CustomOverlay({
        map: map,
        position: markerPosition,
        content: content,
        yAnchor: 1,
      });
    }
  }, [data]);

  // const homepageLink = (
  //   <>
  //     <a href={data.psite} target="_blank" rel="noopener noreferrer">
  //       {data.pname}
  //     </a>
  //   </>
  // );

  return (
    <ParkDetailContainer>
      {isLoading && <div>잠시만 기다려 주세요...</div>}
      {data && (
        <>
          <ParkTitle>
            [{data.parea}] {data.pname}
          </ParkTitle>
          <ParkDetailTop>
            <ParkImage>
              <Image
                width={490}
                height={313}
                src={data.pimg || hasError}
                fallback="data:image/png;base64,"
              />
            </ParkImage>
            <ParkMap>
              <div
                id="kakaoMap"
                style={{
                  width: "490px",
                  height: "313px",
                }}
              ></div>
            </ParkMap>
          </ParkDetailTop>
          <ParkDetailBottom>
            <div className="detailInfo">
              <p className="homepage">
                {`홈페이지 : `}
                {data.psite ? (
                  <a
                    href={data.psite}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    {data.pname}
                  </a>
                ) : (
                  "없음"
                )}
              </p>
              <p> {data.paddr ? `주소 : ${data.paddr}` : ""}</p>
            </div>
            <div className="detailDesc"> {data.pdesc}</div>
          </ParkDetailBottom>
        </>
      )}
    </ParkDetailContainer>
  );
}

const {
  ParkDetailContainer,
  ParkDetailTop,
  ParkDetailBottom,
  ParkTitle,
  ParkImage,
  ParkMap,
} = style;
