import React, { useRef, useEffect, useState } from "react";
import Slider from "react-slick";
import { style } from "./ParkSlide.styles";
import { List, Card, Rate, Image } from "antd";
import { HeartOutlined, HeartFilled } from "@ant-design/icons";
import useGetParkData from "../../hooks/useGetParkData";
import { Link } from "react-router-dom";
export default function ParkSlide() {
  const url = `https://parkproj.herokuapp.com/parks?page=0&size=8&sort=pLikeCnt,desc`;

  const { data, isLoading, hasError, errorMessage } = useGetParkData(url);
  const { Meta } = Card;
  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 4,
  };

  return (
    <ParkSlideContainer>
      <ParkSlideTitle>실시간 베스트 공원</ParkSlideTitle>
      {isLoading && <div>잠시만 기다려 주세요...</div>}
      {hasError && (
        <div>{`공원 데이터를 가져오는데 문제가 있습니다. - ${errorMessage}`}</div>
      )}
      {data && (
        <Slider {...settings}>
          {data.content.map((item) => {
            return (
              <ParkSlideImg key={item.pidx}>
                <Card
                  hoverable
                  cover={
                    <Link to={`/detail/${item.pidx}`}>
                      <Image
                        preview={false}
                        height={200}
                        width={"100%"}
                        src={item.pimg || "error"}
                        alt={item.pname}
                        fallback="data:image/png;base64,"
                      />
                    </Link>
                  }
                >
                  {/* {isLikeClicked ? <HeartFilled /> : <HeartOutlined />} */}
                  <Meta
                    title={
                      <Link to={`/detail/${item.pidx}`}>{item.pname}</Link>
                    }
                  />
                  <Rate disabled defaultValue={item.pavgRate} />
                </Card>
              </ParkSlideImg>
            );
          })}
        </Slider>
      )}
    </ParkSlideContainer>
  );
}
const { ParkSlideContainer, ParkSlideTitle, ParkSlideImg } = style;
